import React, {useEffect, useState} from "react";
import { ethers } from "ethers";
import unicryptETHabi from "../abi/unicrypt_abi.json";

export default Getdata = () => {

    useEffect(() => {
        console.log("wer");
      }, []);

    const unicryptAddressETH = "0x663A5C229c09b049E36dCc11a9B0d4a8Eb9db214";    
    const get_gFees = async () => {
        
        try {
            const { ethereum } = window;
            if (ethereum) {
            //   const provider = new ethers.providers.Web3Provider(ethereum);
            //   const signer = provider.getSigner();
            //   const gFeesPortal = new ethers.Contract(unicryptETHabi);
      
              /*
               * Call the gFees variable from Smart Contract
               */
              //const gFees = await gFeesPortal.getNumLockedTokens();

              let provider = ethers.getDefaultProvider();
              let contract = new ethers.Contract(unicryptAddressETH, unicryptETHabi, provider);
              let gFees = await contract.getNumLockedTokens();
              return gFees;

            } else {
              console.log("Ethereum object doesn't exist!")
            }
        } catch (error) {
            console.log(error);
        }
    }

}
