import React, {useEffect, useState} from "react";
import { useTable, useGlobalFilter, useAsyncDebounce, useSortBy, usePagination } from "react-table";
// import get_gFees from './getdata.js';
import { ethers } from "ethers";
import unicryptETHabi from "../abi/unicryptETH_abi.json";
import uniswapETHabi from "../abi/uniswapETH_abi.json";
import BigNumber from "bignumber.js";
import { createClient } from 'urql'
import Axios from "axios";
import '../Style/style.css';

const unicryptAddressETH = "0x663A5C229c09b049E36dCc11a9B0d4a8Eb9db214";
// const unicryptAddressBSC = "0xC765bddB93b0D1c1A88282BA0fa6B2d00E3e0c83";

function GlobalFilter({
  preGlobalFilteredRows,
  globalFilter,
  setGlobalFilter
}) {
  const count = preGlobalFilteredRows.length;
  const [value, setValue] = React.useState(globalFilter);
  const onChange = useAsyncDebounce(async (value) => {
    setGlobalFilter(value || undefined);
    // let abc = await getAddress();
    // console.log(abc);
  }, 200);

  return (
    <span>
      Search: {" "}
      <input
        value={value || ""}
        onChange={(e) => {
          setValue(e.target.value);
          onChange(e.target.value);
        }}
        placeholder={`Enter Keyword`}
      />
    </span>
  );
}

function Actiontable({ columns, data }) {
  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    footerGroups,
    prepareRow,
    page,
    canPreviousPage,
    canNextPage,
    pageCount,
    gotoPage,
    nextPage,
    previousPage,
    setPageSize,
    state: { pageIndex, pageSize },
    state,
    preGlobalFilteredRows,
    setGlobalFilter
  } = useTable(
    {
      columns,
      data,
      initialState: { pageSize: 20 }
    },
    useGlobalFilter, useSortBy, usePagination
  );

  // console.log();

  // Render the UI for your table
  return (
    <>
      <div className="content">
        <div className="tablesection">
          <table
            {...getTableProps()}
          >
            <thead>
              <tr>
                <th
                  colSpan={100}
                  style={{
                    textAlign: "left",
                    padding: 10,
                    background: "yellow"
                  }}
                >
                  <GlobalFilter
                    preGlobalFilteredRows={preGlobalFilteredRows}
                    globalFilter={state.globalFilter}
                    setGlobalFilter={setGlobalFilter}
                  />
                </th>
              </tr>

              {headerGroups.map((group) => (
                <tr {...group.getHeaderGroupProps()}>
                  {group.headers.map((column) => (
                    <th {...column.getHeaderProps(column.getSortByToggleProps())}>{column.render("Header")}
                    {/* <span>{
                      column.isSorted
                          ? column.isSortedDesc
                                ? ' ?'
                                : ' ?'
                          : ''
                    }</span> */}
                    </th>
                  ))}
                </tr>
              ))}
            </thead>
            <tbody {...getTableBodyProps()}>
              {page.map((row, i) => {
                prepareRow(row);
                return (
                  <tr {...row.getRowProps()}>
                    {row.cells.map((cell) => {
                      return <td {...cell.getCellProps()}>{cell.render("Cell")}</td>;
                    })}
                  </tr>
                );
              })}
            </tbody>
            {/* <tfoot>
              {footerGroups.map((group) => (
                <tr {...group.getFooterGroupProps()}>
                  {group.headers.map((column) => (
                    <td {...column.getFooterProps()}>{column.render("Footer")}</td>
                  ))}
                </tr>
              ))}
            </tfoot> */}
          </table>
        </div>

        <div className="pagination">
          <button onClick={() => gotoPage(0)} disabled={!canPreviousPage}>
            {"<<"}
          </button>{" "}
          <button onClick={() => previousPage()} disabled={!canPreviousPage}>
            {"<"}
          </button>{" "}
          <button onClick={() => nextPage()} disabled={!canNextPage}>
            {">"}
          </button>{" "}
          <button onClick={() => gotoPage(pageCount - 1)} disabled={!canNextPage}>
            {">>"}
          </button>{" "}
          <span>
            Page{" "}
            <strong>
              {pageIndex + 1} of {pageCount}
            </strong>{" "}
          </span>
          <span>
            | Go to page:{" "}
            <input
              type="number"
              defaultValue={pageIndex + 1}
              onChange={(e) => {
                const page = e.target.value ? Number(e.target.value) - 1 : 0;
                gotoPage(page);
              }}
              style={{ width: "100px" }}
            />
          </span>{" "}
          <select
            value={pageSize}
            onChange={(e) => {
              setPageSize(Number(e.target.value));
            }}
          >
            {[20, 30, 40, 50].map((pageSize) => (
              <option key={pageSize} value={pageSize}>
                Show {pageSize}
              </option>
            ))}
          </select>
        </div>
      </div>
    </>
  );
}


async function GetAddress() {
  
  let provider = ethers.getDefaultProvider();
  const unicryptETHPortal = new ethers.Contract(unicryptAddressETH, unicryptETHabi, provider);
  let total_tokenNums = await unicryptETHPortal.getNumLockedTokens();
  let tokenAddr = await unicryptETHPortal.getLockedTokenAtIndex(total_tokenNums - 1);

  const uniswapETHPortal = new ethers.Contract(tokenAddr, uniswapETHabi, provider);
  let token0Addr = await uniswapETHPortal.token0();
  let token1Addr = await uniswapETHPortal.token1();
  let decimals = await uniswapETHPortal.decimals();

  // let apiurl = `https://api.coingecko.com/api/v3/coins/ethereum/contract/${token1Addr}`;
  // const { data: datainfo } = await Axios.get(apiurl);
  // console.log(datainfo.market_data.current_price.usd);


  const APIURL = 'https://api.thegraph.com/subgraphs/name/uniswap/uniswap-v2';
  const ethpriceQuery = `
  query {
    bundle(id: "1" ) {
      ethPrice
    }
  }
`; 

  const tokensQuery = `
    query {
      token(id: `+toString(token1Addr)+`){
        name
        symbol
        decimals
        derivedETH
        tradeVolumeUSD
        totalLiquidity
      }
    }
  `;
  const client = createClient({
    url: APIURL,
  });
  const data1 = await client.query(tokensQuery).toPromise();
  console.log(data1.data.token.symbol);
  const data2 = await client.query(ethpriceQuery).toPromise();
  console.log(data2.data.bundle.ethPrice);




  let tokenLockdata = await unicryptETHPortal.tokenLocks(tokenAddr, 0);
  // let roundAmount = new BigNumber(tokenLockdata[1]._hex).dividedBy(new BigNumber(10).pow(decimals)).toFormat(4);
  let lockedAmount = new BigNumber(tokenLockdata[1]._hex).dividedBy(new BigNumber(10).pow(decimals));

  let total_supply = await uniswapETHPortal.totalSupply();
  let totalSupply = new BigNumber(total_supply._hex).dividedBy(new BigNumber(10).pow(decimals));

  // let percent = new BigNumber(lockedAmount).dividedBy(new BigNumber(totalSupply)).multipliedBy(100).toFormat(1);
  let percent = lockedAmount.dividedBy(totalSupply).multipliedBy(new BigNumber(100)).toFormat(1);

  let lockDate = new BigNumber(tokenLockdata[0]._hex);
  let unlockDate = new BigNumber(tokenLockdata[3]._hex);
  let period = unlockDate.minus(lockDate).dividedBy(new BigNumber(86400)).toFormat(0);

  let roundAmount = lockedAmount.toFormat(4);

  return roundAmount + " (" + percent + "%)";
}


function Table() {
  const [totalSupply, setTotalSupply] = useState("");
    GetAddress().then(resp =>
    {
      setTotalSupply(resp);
    });

  const columns = React.useMemo(
